import { useState, useEffect } from "react";
import axios from "axios";

export default function Search() {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState('');
  const [selectedMovie, setSelectedMovie] = useState(null);

  useEffect(() => {
    if (value) {
      axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=${value}`)
        .then(res => setMovies(res.data.Search || []))
        .catch(error => console.error("Error fetching data:", error));
    }
  }, [value]);

  function Aff(e) {
    e.preventDefault();
    setSelectedMovie(movies.find(movie => movie.Title.toLowerCase() === value.toLowerCase()) || null);
  }

  return (
    <div className="con">
      <form onSubmit={Aff}>
        <input 
          type='text' 
          placeholder='Enter Movie' 
          onChange={(e) => setValue(e.target.value)} 
          className="form-control" 
        />
        <button className="btn btn-primary">Afficher</button>
      </form>
      <div className="row">
        {selectedMovie ? (
          <div className="card" style={{width: "250px"}}>
            <img src={selectedMovie.Poster} className="card-img-top" alt={selectedMovie.Title} />
            <div className="card-body">
              <h6 className="card-title">{selectedMovie.Title}</h6>
              <button href="#" className="btn btn-primary">Details</button>
            </div>
          </div>
        ) : (
          movies.map((movie, i) => (
            <div key={i} className="col-3">
              <div className="card" style={{width: "250px"}}>
                <img src={movie.Poster} className="card-img-top" alt={movie.Title} />
                <div className="card-body">
                  <h6 className="card-title">{movie.Title}</h6>
                  <button href="#" className="btn btn-primary">Details</button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  )
}
