import { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import axios from "axios";
import Home from './Home';
import Search from './Search';
import Serie from './Series';
import Details from './Details';
import './App.css';

export default function App() {
  const [movies, setMovies] = useState([]);
  const [series, setSeries] = useState([]);
  const [search, setSearch] = useState('');

  useEffect(() => {
    // Fetch movies initially
    axios.get('http://www.omdbapi.com/?apikey=4a78675b&s=star')
      .then(res => setMovies(res.data.Search || []));
  }, []);

  useEffect(() => {
    if (searchTerm) {
      // Fetch series when the search term changes
      axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=${searchTerm}&type=series`)
        .then(res => setSeries(res.data.Search || []));
    } else {
      setSeries([]);  // Clear the series list if searchTerm is empty
    }
  }, [searchTerm]);

  return (
    <>
      <BrowserRouter>
        <Search setSearchTerm={setSearch} />
        <Routes>
          <Route path="/" element={<Home movie={movies} />} />
          <Route path="/serie" element={<Serie serie={series} />} />
          <Route path="/movie/:title" element={<Details movie={movies} />} />
        </Routes>
      </BrowserRouter>
    </>
  );
}
