import Aff from "./AfficheSer"
import { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import { Link } from "react-router-dom"
export default function Dserie(props) {
    const { title } = useParams()
    const [text, setText] = useState([])

    useEffect(() => {
        if (props.serie && title) {
            const filterSerie = props.serie.filter(ser => ser.Title === title)
            setText(filterSerie) 
        }
    }, []) 

    return (
        <div>
            {text.length !== 0 
                ? text.map((se, i) => <div className="card" style={{width:"250px"}} key={i}>
                <img src={se.Poster} className="card-img-top" alt={se.Title} />
                <div className="card-body">
                <h6 className="card-title">{se.Title}</h6>
                Year of Release<h5>{se.Year}</h5>
                <Link to={`/serie/${se.Title}`} className="btn btn-primary">Details</Link>
                </div>
                </div>)
                : <p>Movie not found.</p>
            }
        </div>
    )
}
