import { Link } from "react-router-dom";
import Aff from "./Affichage";
import { useState, useEffect } from "react";
import axios from "axios";
import './App.css';
export default function Search(props) {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState('');
  const [selectedMovie, setSelectedMovie] = useState(null);

  // Fetch movies from OMDB API when the search value changes
  useEffect(() => {
    if (value) {
      axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=${value}&type=movie`)
        .then(res => setMovies(res.data.Search || []))
        .catch(error => console.error("Error fetching data:", error));
    }
  }, [value]);

  // Handle search submission and set selected movie
  function handleSearchSubmit(e) {
    e.preventDefault();
    setSelectedMovie(props.movies.find(movie => movie.Title.toLowerCase() === value.toLowerCase()) || null);
  }

  return (
    <nav>
    <div className="header">
        <a className="" href="#">MoviesInfo</a>
        <div className="nav1">
              <Link className="nav11" to={'/'}>Home <span className="sr-only">(current)</span></Link>
              <Link className="nav11" to={'/serie'}>Series</Link>
          <form className="nav11" onSubmit={handleSearchSubmit}>
            <input 
              className="" type="text" placeholder="Search" aria-label="Search" onChange={(e) => { setValue(e.target.value) }} />
          </form>
        </div>

      <div className="container mt-4">
        <div className="row">
          {selectedMovie ? (
            <div className="con">
              <Aff movie={selectedMovie} />
            </div>
          ) : (
            movies.map((movie, i) => (
                <Aff movie={movie} key={i} />
            ))
          )}
        </div>
      </div>
    </div>
    </nav>
  );
}
