import axios from "axios";
import { useState, useEffect } from "react";

export default function Search() {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState("");
  const [selectedMovie, setSelectedMovie] = useState(null);

  useEffect(() => {
    axios.get('http://localhost/php/api/api.php')
      .then(res => setMovies(res.data))
      .catch(error => console.error("Error fetching data:", error));
  }, []);

  function rechercher(e) { 
    e.preventDefault();
    setSelectedMovie(movies.find(movie => movie.Title.toLowerCase() === value.toLowerCase()) || null);
    setSelectedMovie(movies);
  }

  return (
    <>
      <nav className="box">
        <h1>Search</h1>
        <form onSubmit={rechercher}>
          <input type="text" placeholder="Search..." onChange={(e)=>{setValue(e.target.value)}} />
          <input type="submit" value={'Search'} />
        </form>
      </nav>
      <br />

      {selectedMovie ? (
        <div className="card" style={{ width: "18rem" }}>
          <div className="card-body">
            <h5 className="card-title">{selectedMovie.Title}</h5>
            <p className="card-text">{selectedMovie.Description}</p>
          </div>
        </div>
      ) : (
        <p>No movie selected or movie not found.</p>
      )}
    </>
  );
}
