import axios from "axios"
import { useState,useEffect } from "react"
import { Link } from "react-router-dom"
export default function Search() {
  const [products,setproducts]=useState([])
  const [product,setproduct]=useState({})
  const [value,setvalue]=useState("")
  useEffect(()=>{
      axios.get(`http://localhost/php/api/api.php`)
      .then(res=>{setproducts(res.data)}) 
  },[])
  function rechercher(e) { 
      e.preventDefault()
      setproduct(products.find((product)=> { return product.Title===value  }))
   }
    return (
      <>
      <form onSubmit={rechercher}>
          <input type="text" onChange={(e)=>{setvalue(e.target.value)}} className="form-control" />
          <button className="btn btn-primary">rechercher</button>
      </form><br></br>
      <div className="card" style={{width:"250px"}}>
      <img src={product.Image} style={{width:"100%"}}  alt={product.Title} />
        <div className="card-body">
        <Link to={`/product/${product.Title}`} className="aaa"><h6 className="card-title">{product.Title}</h6></Link>
        Year of Release:<p>{product.Year}</p>
        </div>
        </div>
      </>
    )
  }
