import { useDispatch, useSelector } from "react-redux";
import { user, password } from "./action";  // Import actions
import { useState } from "react";

export default function Interface() {
    const dispatch = useDispatch();
    const state = useSelector(state => state); // Accessing the Redux state
    const [isAuthenticated, setIsAuthenticated] = useState(null);

    // Handling user input change for both username and password fields
    const handleUserInput = (event) => {
        const { name, value } = event.target;
        if (name === use) {
            dispatch(user(value)); // Dispatch user action
        } else if (name === "pass") {
            dispatch(password(value)); // Dispatch password action
        }
    };

    const handleVerify = () => {
        const result = state.user === "admin" && state.password === "1234";
        setIsAuthenticated(result);  // Set authentication state
    };
    return (
        <div>
            <input
                type="text"
                name="user"
                value={state.user}
                onChange={handleUserInput}
                placeholder="Enter username"
            />
            <input
                type="password"
                name="pass"
                value={state.password}
                onChange={handleUserInput}
                placeholder="Enter password"
            />
            <button onClick={handleVerify}>Verify</button>

            {isAuthenticated !== null && (
                <div>
                    {isAuthenticated ? <p>Authenticated: true</p> : <p>Authenticated: false</p>}
                </div>
            )}
        </div>
    );
}
