import Home from './Home'
import axios from "axios"
import Search from './Search';
import Serie from './Series';
import Details from './Details'
import './App.css'
import { useState,useEffect } from "react";
import { BrowserRouter,Routes,Route } from 'react-router-dom';
export default function App() {
    const [movies,setmovies]=useState([])
    const [serie,setserie]=useState([])
    
    useEffect(()=>{
        axios.get('http://www.omdbapi.com/?apikey=4a78675b&s=star')
        .then(res=>setmovies(res.data.Search))
    },[])
    useEffect(()=>{
        axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=${serie}&type=series`)
        .then(res=>setserie(res.data.Search))
    },[serie])
return(
    <>
    
        <BrowserRouter>
            <Search />
            <Routes>  
                <Route path={'/'} element={<Home movie={movies} />} />
                <Route path={'/serie'} element={<Serie serie={serie} />} />
                <Route  path={'/movie/:title'} element={<Details movie={movies} />}/>
                <Route  path={'/mov'}/>
            </Routes>
            
        </BrowserRouter>
     </>
)
}
