import axios from "axios";
import { useState, useEffect } from "react";
import Aff from "./Afficher";
import './App.css'
export default function Search() {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState("");
  const [selectedMovie, setSelectedMovie] = useState(null);

  useEffect(() => {
    axios.get('http://localhost/php/api/api.php')
      .then(res => setMovies(res.data))
      .catch(error => console.error("Error fetching data:", error));
  }, []);

  function rechercher(e) {
    e.preventDefault();
    setSelectedMovie(movies.find(movie => movie.Title.toLowerCase() === value.toLowerCase()) || null);
  }
  return (
    <>
      <nav className="box">
        <h1>Search</h1>
        <form onSubmit={handleSearchSubmit}>
          <input type="text" placeholder="Search..." onChange={(e)=>{setValue(e.target.value)}} />
          <input type="submit" value={'Search'} />
        </form>
      </nav>
      <div className="container mt-4">
        <div className="row">
          {selectedMovie ? (
            <Aff />
          ) : (
            movies.map((movie, i) => (
              <Aff movie={movie} key={i}/>
            ))
          )}
        </div>
      </div>
    </>
  );
}
