import { useDispatch, useSelector } from "react-redux";
import { veruser, verpass, verifyCredentials } from "./actions";  // Import the action creators
export default function Interface() {
    const dispatch = useDispatch();
    const user=useSelector(data=>data.user) // Accessing the Redux user
    const pass=useSelector(data=>data.pass)
    // Handle user input change (both username and password fields)
    const handleUserInput = (event) => {
        const { name, value } = event.target;
        if (name === "user") {
            dispatch(user(value)); // Dispatch the user action
        } else if (name === "pass") {
            dispatch(pass(value)); // Dispatch the password action
        }
    };
    return (
        <div>
            <input
                type="text"
                name="user"
                value={user.user}
                onChange={handleUserInput}
                placeholder="Enter username"
            />
            <input
                type="password"
                name="pass"
                value={user.password}
                onChange={handleUserInput}
                placeholder="Enter password"
            />
