            {user.isAuthenticated !== null && (
                <div>
                    {user.isAuthenticated ? (
                        <p>Authenticated: true</p>
                    ) : (
                        <p>Authenticated: false</p>
                    )}
                </div>
            )}

            {user.loginError && <p style={{ color: 'red' }}>{user.loginError}</p>}
        </div>
    );
}
