const initialestate={
    user:'admin',
    password:'1234'
}
const reducer=(state= initialestate,action)=>{
    switch(action.type){
        case "user":
            return{...state,user:state.user=action.payload}
        case "pass":
            return{...state,user:state.password=action.payload}
        default:
            return state
    }
}
export default reducer
