import { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import axios from "axios";
import Home from './Home';
import Search from './Search';
import Serie from './Series';
import Details from './Details';
import Nav from './Nav';
import Watchlist from './Watchlist';
import Dserie from "./Detailsser";
import './App.css';

export default function App() {
  const [movies, setMovies] = useState([]);
  const [watchlist, setWatchlist] = useState([]);

  useEffect(() => {
    axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=star`)
      .then(res => setMovies(res.data.Search || []))
      .catch(error => console.error("Error fetching data:", error));
  }, []);

  const addToWatchlist = (movie) => {
    setWatchlist(prevList => [...prevList, movie]);
  };

  const removeFromWatchlist = (movie) => {
    setWatchlist(prevList => prevList.filter(m => m.Title !== movie.Title));
  };

  return (
    <BrowserRouter>
      <Nav />
      <Routes>
        <Route path="/" element={<Home movie={movies} />} />
        <Route path="/serie" element={<Serie />} />
        <Route path="/movie/:title" element={<Details addToWatchlist={addToWatchlist} />} />
        <Route />
        <Route path="/serie/:title" element={<Dserie />} />
        <Route path="/search" element={<Search movies={movies} />} />
        <Route path="/watchlist" element={<Watchlist watchlist={watchlist} removeFromWatchlist={removeFromWatchlist} />} />
      </Routes>
    </BrowserRouter>
  );
}
