    useEffect(()=>{
        axios.get(`http://localhost/php/api/api.php+${Title}`)
        .then(res=>(set))
    })
    const movie=props.movie.find((p)=>{
        return p.Title===Title
    })
    return (
        <div>
            <p>{text}</p>
            {movie?<p>{movie.Title}</p>:<Aff />}
        </div>
    )
}
