import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Link } from "react-router-dom";
export default function Details() {
  const { title } = useParams();
  const [movie, setMovie] = useState([]);

  useEffect(() => {
    axios
      .get(`http://www.omdbapi.com/?apikey=4a78675b&t=${title}`)
      .then(res => {
        setMovie(res.data);
      })
  }, [title]);
  return (
    <div className="card" style={{width:"360px"}} id="star">
          <img src={movie.Poster} className="card-img-top" alt={movie.Title} id="mog" />
          <h2>{movie.Genre}</h2>
          <div className="card-body">
          <h6 className="card-title">{movie.Title}</h6>
          <p id="mogg">{movie.Plot}</p>
          Year of Release<h5>{movie.Year}</h5>
          <div className="controls">
          <button
            className="btn btn-success">
            Add to Watchlist
          </button> &nbsp; &nbsp;
          <button
            className="btn btn-success">
            Watched
          </button>
        </div>  
          <Link to={`/`} className="btn btn-primary">Home</Link>
          </div>
          </div>
  );
}
