import { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import './App.css';  // Ensure to import your CSS file for styles

export default function Details({ addToWatchlist }) {
  const { Title } = useParams();
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    axios.get(`http://127.0.0.1/php/api.php`)
      .then(res => {
        const movie = res.data.find(m => m.Title.toLowerCase() === Title.toLowerCase());
        setMovie(movie);
      })
  }, [Title]);

  return (
    <div className="">
      {movie ? (
        <div className="card1">
          <div className="card22">
            <img src={movie.Image} alt={movie.Title} style={{width:"100%"}}  />
          </div>
          <div className="card-body1">
            <h3 className="card-title1">{movie.Title}</h3>
            <p>
              Year: <span>{movie.Year}</span><br />
              Genre: <span>{movie.Genre}</span>
            </p>
            <p>Story: <span className="card-description1">{movie.Description}</span></p>
            <div className="eeee">
              <button className="trailer" onClick={() => addToWatchlist(movie)}>Add to Watch List</button><Link to="/" className="trailer"  >Back</Link>
            </div>
          </div>
        </div>
      ) : (
        <p>Movie not found</p>
      )}
    </div>
  );
}
