import Aff from "./Afficher";
export default function Romance(props){
    return(
        <div className="con">
            {
                props.movie.filter((romance)=>{
                    return romance.Genre==='Comedy'
                }).map((mov,i)=>{
                    return <Aff movie={mov} key={i} />
                })
            }
        </div>
    )
}
