import React from 'react';
import { Link } from "react-router-dom";
import Aff from './Affiche';

export default function Watchlist({ watchlist, removeFromWatchlist }) {
  return (
    <div className="container mt-4">
      <h2>Watchlist</h2>
      {watchlist.length === 0 ? (
        <p>No movies in your watchlist.</p>
      ) : (
        <div className="row">
          {watchlist.map((movie, i) => (
            <div key={i} className="col-3">
          
                <img src={movie.Poster} className="card-img-top" alt={movie.Title} />
                <div className="card-body">
                  <h6 className="card-title">{movie.Title}</h6><Link to="/" className="btn btn-light">Home</Link>
                  <Link onClick={() => removeFromWatchlist(movie)} className="btn btn-danger">Supprimer</Link>
                </div>
              </div>
          ))}
        </div>
      )}
      
    </div>
  );
}
