import axios from "axios"
import { Link } from "react-router-dom"
import { useState,useEffect } from "react"
export default function Year() {
    const [movie,setmovie]=useState([])
    const [value,setvalue]=useState('')
    const [text,settext]=useState('')
    useEffect(()=>{
        axios.get(`http://localhost/php/api/api.php`)
        .then(res=>{setmovie(res.data)})
    },[])
    function rechercher(e) { 
        e.preventDefault()
        settext(movie.filter(function (movie) {
            return movie.Year===value
          }).map(function (movie,i) { 
            return(
                <div className="card" style={{width:"250px"}}>
      <img src={movie.Image} style={{width:"100%"}}  alt={movie.Title} />
        <div className="card-body">
        <Link to={`/movie/${movie.Title}`} className="aaa"><h6 className="card-title">{movie.Title}</h6></Link>
        Year of Release:<p>{movie.Year}</p>
        </div>
        </div>
            )
           }))
     }
  return (
    <>
        <form onSubmit={rechercher}>
        <select onChange={(e)=>{setvalue(e.target.value)}}>
            <option value={movie.Year}>movie.Year</option>
            <option value={"electronics"}>electronics</option>
            <option value={"jewelery"}>jewelery</option>
            <option value={"men's clothing"}>men's clothing</option>
            <option value={"women's clothing"}>women's clothing</option>
        </select>
        <button className="btn btn-primary">rechercher</button>
    </form><br></br>
    <div>{text}</div>
    </>
  )
}
