const initialestate={
    watchlist:[]
}

const reducer = (state=initialestate,action)=>{
    switch (action.type) {
        case "ajouter":
            return{...state,watchlist:[...state.watchlist,{
                Title:action.payload.Title,
                Image:action.payload.Image,
                Description:action.Description,
                Genre:action.Genre,
            }]}
        case "supprimer":
            return{...state,watchlist:[...state.watchlist.filter(function (mov) { 
                return mov.Title!==action.payload
             })]}
        default:
            return state

    }
}

export default reducer
