import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import './App.css';

export default function Search() {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState('');
  const [selectedMovie, setSelectedMovie] = useState(null);

  useEffect(() => {
    if (value) {
      axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=${value}&type=movie`)
        .then(res => setMovies(res.data.Search || []))
        .catch(error => console.error("Error fetching data:", error));
    }
  }, [value]);

  function Aff(e) {
    e.preventDefault();
    setSelectedMovie(movies.find(movie => movie.Title.toLowerCase() === value.toLowerCase()) || null);
  }

  return (
    <div className="header1">
          <Link  href="#">MoviesInfo</Link>
          <ul >
            <li>
              <Link to={'/'}>Home <span className="sr-only">(current)</span></Link>
            </li>
            <li >
              <Link className="nav-link" to={'/serie'}>Search Series</Link>
            </li>
            <form onSubmit={Aff}>
            <input type="search" placeholder="Search" aria-label="Search" onChange={(e) => { setValue(e.target.value) }} />
          </form>
          </ul>
      <div className="container">
        <div className="row">
          {selectedMovie ? (
            <div className="col-md-3">
              <div className="card" style={{ width: "18rem" }}>
                <img src={selectedMovie.Poster} className="card-img-top" alt={selectedMovie.Title} />
                <div className="card-body">
                  <h5 className="card-title">{selectedMovie.Title}</h5>
                  <Link to={`/movie/${selectedMovie.imdbID}`}>Details</Link>
                </div>
              </div>
            </div>
          ) : (
            movies.map((movie, i) => (
              <div key={i} className="col-md-3 mb-4">
                <div className="card" style={{ width: "18rem" }}>
                  <img src={movie.Poster} className="card-img-top" alt={movie.Title} />
                  <div className="card-body">
                    <h5 className="card-title">{movie.Title}</h5>
                    <Link to={`/movie/${movie.imdbID}`}>Details</Link>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}
