import axios from "axios";
import { useState, useEffect } from "react";

export default function App() {
  const [series, setSeries] = useState([]);
  const [value, setValue] = useState('');

  // Fetch data whenever the search value changes
  useEffect(() => {
    if (value) {
      axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=${value}&type=series`)
        .then(res => setSeries(res.data.Search || []))
        .catch(error => console.error("Error fetching data:", error));
    }
  }, [value]);

  // Handle search input change
  const handl = (e) => {
    setValue(e.target.value);  
  };

  return (
    <div>
      <div className="collapse navbar-collapse" id="navbarSupportedContent">
        <form className="form-inline my-2 my-lg-0">
          <input 
            className="form-control mr-sm-2" 
            type="search" 
            placeholder="Search" 
            aria-label="Search" 
            value={value} 
            onChange={handl}  
          />
        </form>
      </div>
      <div className="con">
        {series.map((se, i) => (
          <div className="card" style={{ width: "250px" }} key={i}>
            <img src={se.Poster} className="card-img-top" alt={se.Title} />
            <div className="card-body">
              <h6 className="card-title">{se.Title}</h6>
              <a href="#" className="btn btn-primary">Details</a>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
