import React from 'react';
import { Link } from 'react-router-dom';
import './App.css';

export default function Watchlist({ watchlist, removeFromWatchlist }) {
  return (
    <div className='container mt-4'>
      <h2>My Watchlist</h2>
      {watchlist.length === 0 ? (
        <p>No movies in your watchlist.</p>
      ) : (
        <div className='movie-grid'>
          {watchlist.map((movie, i) => (
            <div className="movie-card" key={i}>
              <div className="card">
                <img src={movie.Image} className="card-img-top" alt={movie.Title} />
                <div className="card-body">
                  <h6 className="card-title">{movie.Title}</h6>
                  <p>{movie.Year}</p>
                  <button onClick={() => removeFromWatchlist(movie)} className="btn btn-danger">Supprimer</button>
                  &nbsp;&nbsp;
                  <Link to={`/movie/${movie.Title}`} className="btn btn-primary">Details</Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
