import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import Aff from "./Affiche";
import './App.css';
export default function Search(props) {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState('');
  const [selectedMovie, setSelectedMovie] = useState(null);

  // Fetch movies from OMDB API when the search value changes
  useEffect(() => {
    if (value) {
      axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=${value}`)
        .then(res => setMovies(res.data.Search || []))
        .catch(error => console.error("Error fetching data:", error));
    }
  }, [value]);

  // Handle search submission and set selected movie
  function handleSearchSubmit(e) {
    e.preventDefault();
    setSelectedMovie(props.movies.find(movie => movie.Title.toLowerCase() === value.toLowerCase()) || null);
  }

  return (
<>

      <nav className="navbar navbar-expand-lg " id="header">
        <nav className="container-fluid" id="nav11">
            <Link className="navbar-brand" id="logo" to={'/'}><p><i id="id">M</i>oviesInfo</p></Link>
          <ul className="navbar-nav me-auto mb-0" >
            <li className="nav-item">
              <Link className="nav-link active" id="mmm"  aria-current="page" to={'/'}><p>Home</p></Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link active" id="mmm" aria-current="page" to={'/serie'}><p>Series</p></Link>
            </li>
          </ul>
          <div className="add-content">
                    <div className="input-container">
                        <input type="text" placeholder="Search for a Movie" value={searchValue} 
                            onChange={(e)=>{setsearchValue(e.target.value)}}/>
                    </div>
                   {
                        movies.length > 0 && (
                            <ul className='results'>
                                {
                                    movies.map((movie)=>{
                                      return(
                                        <>
                                        <li key={movie.imdbID}>
                                            {<ResaultCard movie={movie}/>}
                                        </li>
                                        </>
                                      )  
                                    })
                                }
                            </ul>
                        )
                   }
                </div>
      </nav> 
      </nav>
      </>
  );
}
