import { useDispatch, useSelector } from "react-redux";
import { user, password, verifyCredentials } from "./actions";  // Import the action creators

export default function Interface() {
    const dispatch = useDispatch();
    const state = useSelector(state => state); // Accessing the Redux state

    // Handle user input change (both username and password fields)
    const handleUserInput = (event) => {
        const { name, value } = event.target;
        if (name === "user") {
            dispatch(user(value)); // Dispatch the user action
        } else if (name === "pass") {
            dispatch(password(value)); // Dispatch the password action
        }
    };

    // Verify credentials action when the button is clicked
    const handleVerify = () => {
        // Dispatch the verifyCredentials action
        dispatch(verifyCredentials());
    };

    return (
        <div>
            <input
                type="text"
                name="user"
                value={state.user}
                onChange={handleUserInput}
                placeholder="Enter username"
            />
            <input
                type="password"
                name="pass"
                value={state.password}
                onChange={handleUserInput}
                placeholder="Enter password"
            />
            <button onClick={handleVerify}>Verify</button>

            {state.isAuthenticated !== null && (
                <div>
                    {state.isAuthenticated ? (
                        <p>Authenticated: true</p>
                    ) : (
                        <p>Authenticated: false</p>
                    )}
                </div>
            )}

            {state.loginError && <p style={{ color: 'red' }}>{state.loginError}</p>}
        </div>
    );
}
