import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import './App.css';

export default function Search() {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState('');
  const [selectedMovie, setSelectedMovie] = useState(null);

  useEffect(() => {
    if (value) {
      axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=${value}&type=movie`)
        .then(res => setMovies(res.data.Search || []))
        .catch(error => console.error("Error fetching data:", error));
    }
  }, [value]);

  function Aff(e) {
    e.preventDefault();
    setSelectedMovie(movies.find(movie => movie.Title.toLowerCase() === value.toLowerCase()) || null);
  }

  return (
    <div className="header">
        <a className="navbar-brand" href="#">MoviesInfo</a>
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav mr-auto">
            <li className="nav-item active">
              <Link className="nav-link" to={'/'}>Home <span className="sr-only">(current)</span></Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link" to={'/serie'}>Search Series</Link>
            </li>
          </ul>
          <form className="form-inline my-2 my-lg-0" onSubmit={Aff}>
            <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" onChange={(e) => { setValue(e.target.value) }} />
          </form>
        </div>

      <div className="container mt-4">
        <div className="row">
          {selectedMovie ? (
            <div className="col-md-3">
              <div className="card" style={{ width: "18rem" }}>
                <img src={selectedMovie.Poster} className="card-img-top" alt={selectedMovie.Title} />
                <div className="card-body">
                  <h5 className="card-title">{selectedMovie.Title}</h5>
                  <Link to={`/movie/${selectedMovie.imdbID}`}>Details</Link>
                </div>
              </div>
            </div>
          ) : (
            movies.map((movie, i) => (
              <div key={i} className="col-md-3 mb-4">
                <div className="card" style={{ width: "18rem" }}>
                  <img src={movie.Poster} className="card-img-top" alt={movie.Title} />
                  <div className="card-body">
                    <h5 className="card-title">{movie.Title}</h5>
                    <Link to={`/movie/${movie.imdbID}`}>Details</Link>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}
