import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export default function Details() {
  const { Title } = useParams();
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost/php/api/api.php`)
      .then(res => {
        const movie = res.data.find(m => m.Title.toLowerCase() === Title.toLowerCase());
        setMovie(movie);
      })
      .catch(error => console.error("Error fetching data:", error));
  }, [Title]);

  return (
    <div className="container mt-4">
      {movie ? (
        <div className="card">
          <div className="card-header">
            <h2>{movie.Title}</h2>
          </div>
          <div className="card-body">
            <img src={movie.Poster} alt={movie.Title} className="img-fluid mb-4" />
            <p><strong>Year:</strong> {movie.Year}</p>
            <p><strong>Genre:</strong> {movie.Genre}</p>
            <p><strong>Plot:</strong> {movie.Image}</p>
          </div>
          <div className="card-footer">
            <a href="/" className="btn btn-primary">Back to Home</a>
          </div>
        </div>
      ) : (
        <p>Movie not found</p>
      )}
    </div>
  );
}
