import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Link } from "react-router-dom";
export default function Dserie() {
  const { title } = useParams();
  const [serie, setSerie] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get(`http://www.omdbapi.com/?apikey=4a78675b&t=${title}&type=series`)
      .then(res => {
        setSerie(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error("Error fetching series details:", err);
        setLoading(false);
      });
  }, [title]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!serie) {
    return <div>Series not found.</div>;
  }

  return (
    <div className="card" style={{width:"360px"}} id="star">
          <img src={serie.Poster} className="card-img-top" alt={serie.Title} id="mog" />
          <h2>{serie.Genre}</h2>
          <div className="card-body">
          <h6 className="card-title">{serie.Title}</h6>
          <p id="mogg">{serie.Plot}</p>
          Year of Release<h5>{serie.Year}</h5>
          <div className="controls">
          <button
            className="btn btn-success">
            Add to Watchlist
          </button> &nbsp; &nbsp;
          <button
            className="btn btn-success">
            Add to Watched
          </button>
        </div>  
          <Link to={`/serie`} className="btn btn-primary">Serie</Link>
          </div>
          </div>
  );
}
