import { useDispatch, useSelector } from "react-redux"
import { supprimer } from "./Actions";
export default function Panier() {
    const panier = useSelector(data=>data.panier)
    const dispatch = useDispatch()
    const totals = panier.reduce(function (total,product) {
        return total+=product.price*product.quantite
      },0)
  return (
    <>
        <table className="table">
    <th>Title</th><th>Price</th><th>quantite</th><th>Actions</th>
    {
        panier.map(function (product,i) { 
        return(
            <tr>
                <td>{product.title}</td>
                <td>{product.price}</td>
                <td>{product.quantite}</td>
                <td><button onClick={()=>dispatch(supprimer(product.id))} className="btn btn-danger">supprimer</button></td>
            </tr>
        )
     })
    }
    </table>
    <p>{totals}</p>
    </>
  )
}
