import { useState, useEffect } from "react";
import axios from "axios";

export default function Search() {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState('');
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [year, setYear] = useState('');

  useEffect(() => {
    if (value || year) {
      axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=${value}&y=${year}`)
        .then(res => setMovies(res.data.Search || []))
        .catch(error => console.error("Error fetching data:", error));
    }
  }, [value, year]);

  function handleYearChange(e) {
    setYear(e.target.value);
  }

  function Aff(e) {
    e.preventDefault();
    setSelectedMovie(movies.find(movie => movie.Title.toLowerCase() === value.toLowerCase()) || null);
  }

  return (
    <nav className="navbar navbar-inverse">
      <div className="container-fluid">
        <div className="navbar-header">
          <a className="navbar-brand" href="#">MoviesInfo</a>
        </div>
        <form onSubmit={Aff} className="navbar-form navbar-left">
          <div className="input-group">
            <input type='text' placeholder='Enter Movie' onChange={(e) => setValue(e.target.value)} className="form-control" />
            <div className="input-group-btn">
              <button className="btn btn-default" type="submit">
                <i className="ri-search-line"></i>
              </button>
            </div>
          </div>
        </form>
        <div className="row">
          {selectedMovie ? (
            <div className="col-3">
              <div className="row">
                <div className="card" style={{ width: "250px" }}>
                  <img src={selectedMovie.Poster} className="card-img-top" alt={selectedMovie.Title} />
                  <div className="card-body">
                    <h5 className="card-title">{selectedMovie.Title}</h5>
                    <a href="#" className="btn btn-primary">buy</a>
                  </div>
                </div>
              </div>
            </div>
          ) : (
            movies.map((movie, i) => (
              <div key={i} className="col-3">
                <div className="card" style={{ width: "250px" }}>
                  <img src={movie.Poster} className="card-img-top" alt={movie.Title} />
                  <div className="card-body">
                    <h5 className="card-title">{movie.Title}</h5>
                    <a href="#" className="btn btn-primary">Details</a>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </nav>
  );
}
