import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Link } from "react-router-dom";
export default function Details() {
  const { title } = useParams();
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get(`http://www.omdbapi.com/?apikey=4a78675b&t=${title}`)
      .then(res => {
        setMovie(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error("Error fetching movie details:", err);
        setLoading(false);
      });
  }, [title]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!movie) {
    return <div>Movie not found.</div>;
  }

  return (
    <div className="card" style={{width:"36400px"}} id="star">
          <img src={movie.Poster} className="card-img-top" alt={movie.Title} id="mog" />
          <div className="card-body">
          <h6 className="card-title">{movie.Title}</h6>
          <p>{movie.Plot}</p>
          Year of Release<h5>{movie.Year}</h5>
          <Link to={`/movie/${movie.Title}`} className="btn btn-primary">Details</Link>
          </div>
          </div>
  );
}
