export default function Useref() {
    const nom=useRef("")
    const op=useRef("")
    const f=useRef("")
    const m=useRef("")
    const [text,settext]=useState("")
    function afficher(e) {
        e.preventDefault()
        settext(nom.current.value+' '+op.current.value+'')
        if (m.current.checked) {
            settext(m.current.value)
        }else{
            settext(f.current.value)
        }
    }
  return (
    <form onSubmit={afficher}>
        <input type="text" ref={nom}/>
        <select ref={op}>
            <option value={''}>-----</option>
            <option value={"a"}>A</option>
            <option value={"b"}>B</option>
            <option value={"c"}>C</option>
            <option value={"d"}>D</option>
        </select>
            F<input type="radio" value={"f"} name="sex" ref={f}/>
            M<input type="radio" value={"m"} name="sex" ref={m}/>        
        <button>afficher</button>
        <h2>{text}</h2>
    </form>
  )
}
