import React from 'react';
import { Link } from 'react-router-dom';
import './App.css'; // Ensure this import is at the top to apply your styles

export default function Watchlist({ watchlist,removeFromWatchlist}) {
  return (
    <>
      {watchlist.length === 0 ? (
        <p className='text-center'>No movies in your watchlist.</p>
      ) : (
        <div className='con'>
          {watchlist.map((mov,i)=>{
              <div className="card" style={{width:"250px"}} key={i} id="messi">
                  <img src={mov.Poster} className="card-img-top" alt={mov.Title} />
              <div className="card-body">
              <h6 className="card-title">{mov.Title}</h6>
              Year of Release<h5>{mov.Year}</h5>
              <Link to={`/movie/${mov.Title}`} className="">Details</Link>
              <button onClick={(()=>{removeFromWatchlist(mov)})}></button>
              </div>
              </div>
          })}
        </div>
      )}
    </>
  );
}
