import { useEffect, useState } from 'react'
import {useParams} from 'react-router-dom'
import Aff from './Afficher'
export default function Details(props){
    const {Title}=useParams()
    const [text,settext]=useState('')
    useEffect(()=>{
        settext(props.movie.filter((m)=>{
            return m.Title===Title.toLowerCase()
        }).map((pam)=>{ <Aff movie={pam} />}))
    },[])
    const movie=props.movie.find((p)=>{
        return p.Title===Title
    })
    return (
        <div>
            <p>{text}</p>
            {movie?<p>{mo}</p>:Error}
        </div>
    )
}
