const initialestate={
    user:'admin',
    pass:'1234'
}
const reducer = (state = initialestate, action) => {
    switch (action.type) {
        case "user":
            return { ...state,user: action.payload };
        case "pass":
            return { ...state, password: action.payload };
        case "verify":
            const verify = { user: 'admin', password: '1234' };
            if (state.user === Verify.user && state.password === Verify.password) {
                return(
                    <p>Vrai</p>
                )
            }else{
                return <p>Faux</p>
            }
        default:
            return state
    }
    
}
export default reducer
