import { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";

export default function Details() {
  const { Title } = useParams();
  const [movie, setmovie] = useState({});

  useEffect(() => {
    axios
      .get(`http://www.omdbapi.com/?apikey=4a78675b&t=${Title}`)
      .then(res => {
        setmovie(res.data);
      });
  }, [Title]);

  return (
    <div className="card1">
      <div className="card-thumbnail1">
        <img src={serie.Poster} alt={serie.Title} />
      </div>
      <div className="card-body1">
        <h2 className="card-tTitle1">{serie.Title}</h2>
        <p>
          Year: <span>{serie.Year}</span><br />
          Genre: <span>{serie.Genre}</span>
        </p>
        <p>Story: <span className="card-description1">{movie.Description}</span></p>
        <div className="eeee">
          <button className="trailer" onClick={() => addToWatchlist(serie)}>Add Watch List</button>
        </div>
        <Link to={`/serie`} className="btn btn-light" id="click">Retour</Link>
      </div>
    </div>
  );
}
