import axios from "axios";
import { useState, useEffect } from "react";
export default function SearchV() {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState("");
  const [selectedMovies, setSelectedMovies] = useState([]);

  useEffect(() => {
    if (value) {
      axios
        .get(`http://www.omdbapi.com/?apikey=4a78675b&s=${value}`)
        .then((res) => setMovies(res.data.Search || []))
        .catch((error) => console.error("Error fetching data:", error));
    }
  }, [value]);

  function rechercher(e) {
    e.preventDefault();
    const filteredMovies = movies.filter((mov) => mov.Title.toLowerCase() === value.toLowerCase());
    setSelectedMovies(filteredMovies);
  }

  return (
    <>
      <form onSubmit={rechercher} className="col-sm">
        <input
          type="text"
          onChange={(e) => setValue(e.target.value)}
          className=""
          placeholder="Enter movie title"
        />
        <button>Rechercher</button>
      </form>
      <br />
      {selectedMovies.length > 0 ? (
        selectedMovies.map((movie,i) => (
          <div key={i}>
            <p>{movie.Title}</p>
            <img src={movie.Poster} alt={movie.Title} />
          </div>
        ))
      ) : (
        <p>{value ? "Aucune film trouvé" : ""}</p>
      )}
    </>
  );
}
