import React from 'react';
import { Link } from 'react-router-dom';
export default function Watchlist({ watchlist, removeFromWatchlist }) {
  return (
    <div className='con'>
      <h2>Watchlist</h2>
      {watchlist.length === 0 ? (
        <p>No movies in your watchlist.</p>
      ) : (
        <div className='con'>
          {watchlist.map((movie, i) => (
              <div className="card" style={{ width: "250px" }}>
                <img src={movie.Poster} className="card-img-top" alt={movie.Title} />
                <div className="card-body">
                  <h6 className="card-title">{movie.Title}</h6>
                  <button onClick={() => removeFromWatchlist(movie)} className="trailer btn btn-danger">Supprimer</button><Link to={`/movie/${movie.Title}`} className="trailer">Details</Link>
                </div>
              </div>
          ))}
        </div>
      )}
      
    </div>
  );
}
