import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Aff from './Afficher';

export default function Details(props) {
  const { Title } = useParams();
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    const foundMovie = props.movie.find((m) => m.Title.toLowerCase() === Title.toLowerCase());
    if (foundMovie) {
      setMovie(foundMovie);
    }
  }, [Title, props.movie]);

  return (
    <div>
      {movie ? (
        <div>
          <h2>{movie.Title}</h2>
          <p>Year: {movie.Year}</p>
          <p>Genre: {movie.Genre}</p>
          <p>Plot: {movie.Image}</p>
          <img src={movie.Image} alt={movie.Title} />
        </div>
      ) : (
        <Aff />
      )}
    </div>
  );
}
