import Aff from "./Afficher";
export default function Adventure(props){
    return(
        <div className="con">
            {
                props.movie.filter((adv)=>{
                    return adv.Genre==='Adventure'
                }).map((mov,i)=>{
                    return <Aff movie={mov} key={i} />
                })
            }
        </div>
    )
}
