import { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import axios from "axios";
import Home from './Home'
import Nav from "./Nav";
import Horror from "./Horror";
import './App.css';
import Action from "./Action";
import Drama from "./Drama";
import Comedy from './Comedy';
import Details from "./Details";
import Rechercherinput from './Search'
import War from './War'
import Crime from "./Crime";
export default function App() {
  const [movies, setmovies] = useState([]);
  useEffect(() => {
    axios.get(`http://localhost/php/api/api.php`)
      .then(res => setmovies(res.data))
  }, []);

  return (
    <BrowserRouter>
    <Nav />
      <Routes>
        <Route path="/" element={<Home movie={movies} />} />
        <Route path={'/Action'} element={<Action movie={movies} />} />
        <Route path={'/Horror'} element={<Horror movie={movies} />} />
        <Route path={'/Drama'} element={<Drama movie={movies}/> } />
        <Route path={'/Comedy'} element={<Comedy movie={movies} />}/>
        <Route path={'/Crime'} element={<Crime movie={movies} />}/>
        <Route path={'/War'} element={<War movie={movies} />}/>
        <Route path={'/Mystery'} element={<Mystery movie={movies} />}/>
        <Route path="/movie/:Title" element={<Details movie={movies} />} />
        <Route path="/Rechercherinput" element={<Rechercherinput movie={movies} />}/>
      </Routes>
    </BrowserRouter>
  );
}
