import { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import './App.css';  // Ensure to import your CSS file for styles

export default function Details({ addToWatchlist }) {
  const { Title } = useParams();
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost/php/api/api.php`)
      .then(res => {
        const movie = res.data.find(m => m.Title.toLowerCase() === Title.toLowerCase());
        setMovie(movie);
      })
      .catch(error => console.error("Error fetching data:", error));
  }, [Title]);

  return (
    <div className="con">
      {movie ? (
        <div className="card1">
          <div className="card22">
            <img src={movie.Image} alt={movie.Title} className="img-fluid" />
          </div>
          <div className="card-body1">
            <h2 className="card-title1">{movie.Title}</h2>
            <p>
              Year: <span>{movie.Year}</span><br />
              Genre: <span>{movie.Genre}</span>
            </p>
            <p>Story: <span className="card-description1">{movie.Description}</span></p>
            <div className="eeee">
              <button className="trailer btn btn-primary" onClick={() => addToWatchlist(movie)}>Add to Watch List</button><Link to="/" className="trailer btn btn-primary" id="click">Back</Link>
            </div>
            
          </div>
        </div>
      ) : (
        <p>Movie not found</p>
      )}
    </div>
  );
}
