import React from 'react';
import Aff from './Affiche';
import './App.css'; // Ensure this import is at the top to apply your styles

export default function Watchlist({ watchlist,}) {
  return (
    <div className='container mt-4' id='con'>
      
      {watchlist.length === 0 ? (
        <p className='text-center'>No movies in your watchlist.</p>
      ) : (
        <div className='row' id='row'><h2>Watchlist</h2>
          {watchlist.map((movie, i) => (
            <>
            <Aff movie={movie} key={i} />
            <button onClick={() => removeFromWatchlist(movie)} className="btn btn-danger">Supprimer</button></>
          ))}
        </div>
      )}
      <div className='text-center'>
      </div>
    </div>
  );
}
