import { useState, useEffect } from "react";
import axios from "axios";
import './App.css'
export default function Search() {
  const [movies, setMovies] = useState([]);
  const [value, setValue] = useState('');
  const [selectedMovie, setSelectedMovie] = useState(null);
  useEffect(() => {
    if (value) {
      axios.get(`http://www.omdbapi.com/?apikey=4a78675b&s=${value}`)
        .then(res => setMovies(res.data.Search || []))
        .catch(error => console.error("Error fetching data:", error));
    }
  }, [value]);
  function Aff(e) {
    e.preventDefault();
    setSelectedMovie(movies.find(movie => movie.Title.toLowerCase() === value.toLowerCase()) || null);
  }

  return (
    <nav className="navbar navbar-light bg-light ">
          <a className="navbar-brand" href="#">MoviesInfo</a>
        <form onSubmit={Aff} className="form-inline" >
            <input type='text' placeholder='Enter Movie' onChange={(e) => setValue(e.target.value)} className="form-control mr-sm-2" />

              <a className="" type="submit">
                <i className="fa fa-search"></i>
              </a>
            >
        </form>
        <div className="con">
          <p>dgffg</p>
          {selectedMovie ? (
                <div className="card" style={{width:"250px"}}>
                  <img src={selectedMovie.Poster} className="card-img-top" alt={selectedMovie.Title} />
                  <div className="card-body">
                    <h6 className="card-title">{selectedMovie.Title}</h6>
                    <a>Details</a>
                  </div>
            </div>
          ) : (
            movies.map((movie, i) => (
              <div key={i} className="con">
                <div className="card" style={{ width: "250px" }}>
                  <img src={movie.Poster} className="card-img-top" alt={movie.Title} />
                  <div className="card-body">
                    <h6 className="card-title">{movie.Title}</h6>
                    <a href="#" className="btn btn-primary">Details</a>
                  </div>
                </div>
              </div>
            ))
          )}
          </div>
    </nav>
  );
}
