import axios from "axios";
import { useState, useEffect } from "react";

export default function Rechercherinput() {
  const [movies, setMovies] = useState([]);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [value, setValue] = useState("");

  useEffect(() => {
    axios.get(`http://localhost/php/api/api.php`)
      .then(res => setMovies(res.data))
      .catch(error => console.error("Error fetching data:", error));
  }, []);

  function rechercher(e) { 
    e.preventDefault();
    const movie = movies.find((movie) => movie.Title.toLowerCase() === value.toLowerCase());
    setSelectedMovie(movie);
  }

  return (
    <>
      <form onSubmit={rechercher}>
        <input 
          type="text" 
          onChange={(e) => setValue(e.target.value)} 
          className="form-control" 
          placeholder="Enter movie title"
        />
        <button className="btn btn-primary" type="submit">Rechercher</button>
      </form>
      <br />

      {selectedMovie ? (
        <div className="card" style={{width:"250px"}}>
        <img src={movie.Image} style={{width:"100%"}}  alt={movie.Title} />
          <div className="card-body">
          <Link to={`/movie/${movie.Title}`} className="aaa"><h6 className="card-title">{movie.Title}</h6></Link>
          Year of Release:<p>{movie.Year}</p>
          </div>
          </div>
      ) : (
        <p>No movie selected or movie not found.</p>
      )}
    </>
  );
}
