          <div className="card-body">
          <h6 className="card-title">{se.Title}</h6>
          Year of Release<h5>{se.Year}</h5>
          <Link to={`/serie/${se.Title}`} className="btn btn-primary">Details</Link>
          </div>
          </div>
  );
}
