import Affichage from "./Affiche"
import axios from "axios"
import { useState,useEffect } from "react"
// export default function movie(props){
//     return(
//         props.movie.map(function(rec,i){
//             return <Affichage movie={rec} key={i}/>
//         })
//     )
// }
export default function SearchV(props) {
const [movie,setmovie]=useState([])
const [value,setvalue]=useState("")
useEffect(()=>{
    axios.get('http://www.omdbapi.com/?apikey=4a78675b&s=${value}')
    .then(res=>setmovie(res.data.Search))
},[value])
function rechercher(e) { 
    e.preventDefault()
    setmovie(props.movie.filter(function (mov) { return mov.Year===value  })).map((mov,i)=>{
        return <Affichage movie={mov} key={i} />
    })
 }
  return (
    <>
    <form onSubmit={rechercher}>
        <input type="text" onChange={(e)=>{setvalue(e.target.value)}} className="form-control" />
        <button className="btn btn-primary">rechercher</button>
    </form><br></br>

    
    </>
  )
}
