import React from 'react';
import { Link } from 'react-router-dom';
import './App.css'; // Ensure this import is at the top to apply your styles

export default function Watchlist({ watchlist,removeFromWatchlist}) {
  return (
    < >
      
      {watchlist.length === 0 ? (
        <p className='text-center'>No movies in your watchlist.</p>
      ) : (
        <div className='con' >
           {watchlist.map((movie, i) => (
             <div id='connn' style={{width:"300px"}} key={i} >
               <div className="card" style={{ width: "250px" }}>
                 <img src={movie.Poster} className="card-img-top" alt={movie.Title} />
                <div className="card-body"> 
                    <h6 className="card-title">{movie.Title}</h6>
                    Year of Release<h5>{movie.Year}</h5>
                       <button onClick={() => removeFromWatchlist(movie)} className="btn btn-danger">Supprimer</button>&nbsp;&nbsp;
                       <Link to={`/movie/${movie.Title}`} className="click">Details</Link>
                        </div> </div> </div> ))}
                 </div>
      )}
      <div className='text-center'>
      </div>
    </>
  );
}
