import React from 'react';
import { Link } from 'react-router-dom';
import './App.css'; // Ensure this import is at the top to apply your styles

export default function Watchlist({ watchlist, removeFromWatchlist }) {
  return (
    <div className='container mt-4'>
      
      {watchlist.length === 0 ? (
        <p className='text-center'>No movies in your watchlist.</p>
      ) : (
        <div className='row'><h2 className='text-center' style={{color:"white"}}>Watchlist</h2>
          {watchlist.map((movie, i) => (
            <div className="col-md-3" key={i}>
              <div className="card">
                <img src={movie.Poster} className="card-img-top" alt={movie.Title} />
                <div className="card-body">
                  <h6 className="card-title">{movie.Title}</h6>
                  <button onClick={() => removeFromWatchlist(movie)} className="trailer1">Supprimer</button>
                  <Link to={`/movie/${movie.Title}`} className="trailer">Details</Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
      <div className='text-center'>
      </div>
    </div>
  );
}
