import React from 'react';
import { Link } from 'react-router-dom';
import './App.css'; // Ensure this import is at the top to apply your styles

export default function Watchlist({ watchlist, removeFromWatchlist }) {
  return (
    <div className="container mt-4 con">
      <h2 className='text-center'>Watchlist</h2>
      {watchlist.length === 0 ? (
        <p className='text-center'>No movies in your watchlist.</p>
      ) : (
        <div id="row">
          {watchlist.map((movie, i) => (
            <div className="card" style={{ width: "250px" }} key={i} id="messi">
              <img src={movie.Pposter} className="card-img-top" alt={movie.Title} />
              <div className="card-body">
                <h6 className="card-title">{movie.Title}</h6>
                <p>Year of Release: <strong>{movie.Year}</strong></p>
                <Link to={`/movie/${movie.Title}`} className="btn btn-primary">Details</Link>
                <button onClick={() => removeFromWatchlist(movie)} className="btn btn-danger">Supprimer</button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
